import styled from 'styled-components'

const StyledWork = styled.section`
  height: 50vw;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  padding-top: 2vw;
  padding-bottom: 2vw;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  text-align: center;
  background-color: var(--wine);
`

const HeaderContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  background-color: var(--wine);
`

const Header = styled.h2`
  margin: 0.75rem;
  font-size: 8vw;
  font-weight: 900;
  font-style: italic;
  line-height: 14vw;
  background: -webkit-linear-gradient(var(--purple), var(--coral));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`

export { StyledWork, HeaderContainer, Header }
