import styled from 'styled-components'


const StackContainer = styled.section`
width: 100%;
display: flex;
flex-wrap: wrap;
justify-content: center;
text-align: center;

h3 {
    text-align: left;
    font-size: 4vw;
    margin-bottom: 0;
    margin-left: 1vw;
    font-weight: 200;
    color: #a6c0fe;
}
`

const StackCard = styled.div`
min-height: 10vw;
max-width: 60%;
padding: 0.75rem;
border-radius: 1vw;
display: flex;
flex-wrap: wrap;
justify-content: center;
align-items: center;
background-color: #a6c0fe;
margin: 1rem;
@media (max-width: 575px) {
    justify-content: space-around;
  }
`

const StackIcon = styled.div`
display: flex;
padding: 0.5rem;

div {
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-right: 1rem;
    margin-left: 1rem;
    width: 4vw;
}

/* Icon Labels */
div > div > span {
    font-size: 0.8rem;
    color: white;
}

/* Icons */
svg {
    width: 100%;
    max-width: 90px;
    margin: 0.5vw;
}

`


export { StackContainer, StackCard, StackIcon }
