import React, { Component } from 'react'

import {Card} from 'react-bootstrap'

import {
  ProjectContainer,
  ProjectImage,
  ProjectOverlay
} from './Project.styles'

class Project extends Component {
  state = { hovered: false }

  mouseOver = () => this.setState({ hovered: true })
  mouseOut = () => this.setState({ hovered: false })

  render() {
    if (this.props.name === 'tenners') {
      return (
        <ProjectContainer
          onMouseOver={this.mouseOver}
          onMouseOut={this.mouseOut}
        >
          <ProjectImage src="https://res.cloudinary.com/fsuffieldcode/image/upload/v1584229261/tenners-responsive-mockup.jpg" />

          {this.state.hovered === true ? <ProjectOverlay /> && console.log('hovered') : null}



          <Card>
  <Card.Header>Quote</Card.Header>
  <Card.Body>
    <blockquote className="blockquote mb-0">
      <p>
        {' '}
        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer posuere
        erat a ante.{' '}
      </p>
      <footer className="blockquote-footer">
        Someone famous in <cite title="Source Title">Source Title</cite>
      </footer>
    </blockquote>
  </Card.Body>
</Card>
        </ProjectContainer>

      )
    }
  }
}

export default Project
