import { createGlobalStyle } from 'styled-components'

const GlobalStyle = createGlobalStyle`
    html {
        overflow: hidden;
        height: 100%;
    }
    body {
        height: 100%;
        overflow: auto;
        font-family: 'Muli', sans-serif;
        font-weight: 900;
        box-sizing: border-box;
        background-color: whitesmoke;
    }
`

export { GlobalStyle }
