import styled from 'styled-components'

const StyledWork = styled.section`
  height: 50vw;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  margin-top: 5vw;
  margin-bottom: 5vw;
  padding-top: 2vw;
  padding-bottom: 2vw;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  text-align: center;


`

const HeaderContainer = styled.div`
width: 100%;
display: flex;
justify-content: center;
`

const Header = styled.h2`
  color: var(--wine);
`

export { StyledWork, HeaderContainer, Header }
