import styled from 'styled-components'


const Main = styled.main`
width: 100vw;
height: 100%;
`

const MainContainer = styled.div`
width: 90%;
max-width: 1100px;
margin-left: auto;
margin-right: auto;

@media (min-width: 1200px) {
    width: 95%;
}
`


export { Main, MainContainer }
