import styled from 'styled-components'


const StackContainer = styled.section`
width: 100%;
padding-top: 2vw;
padding-bottom: 2vw;
display: flex;
flex-wrap: wrap;
justify-content: center;
text-align: center;


/* Stack labels */
h3 {
    text-align: center;
    font-size: 2.7vw;
    margin-bottom: 0;
    font-weight: 200;
    color: #6e6e6e;
}
`

const StackCard = styled.div`
min-height: 10vw;
max-width: 16vw;
padding: 0.8rem;
margin: 2vw;
border-radius: 1vw;
display: flex;
flex-wrap: wrap;
justify-content: space-around;
align-items: center;
border: 1px solid var(--lightgrey);
background-color: white;

@media (min-width: 900px) {
        max-width: 16vw;
}
`

const StackIcon = styled.div`
display: flex;
margin: 0.4rem;

div {
    display: flex;
    flex-direction: column;
    align-items: center;
    margin-right: 1rem;
    margin-left: 1rem;
    width: 4vw;
}

/* Icon Labels */
div > div > span {
    font-size: 0.8rem;
}

/* Icons */
svg {
    width: 5vw;
    max-width: 90px;
    margin: 0.5vw;

}

svg > div > span {
    font-size: 0.2rem;
}

@media (min-width: 900px) {
    svg {
        width: 4vw;
    }
}

`


export { StackContainer, StackCard, StackIcon }
