import { createGlobalStyle } from 'styled-components'

const GlobalStyle = createGlobalStyle`
    html {
        overflow: hidden;
        height: 100%;
    }
    body {
        height: 100%;
        overflow: auto;
        font-family: 'Muli', sans-serif;
        box-sizing: border-box;
        background-image: linear-gradient(to top, #cfd9df 0%, #e2ebf0 100%);
    }
`

export { GlobalStyle }
