import React, { Component } from 'react'
import styled from 'styled-components'

// Styles

const StyledNav = styled.nav`
  width: 100%;
  min-height: 15vh;
  display: flex;
  justify-content: center;
  padding-top: 0.5rem;
  padding-bottom: 0.5rem;
  color: var(--darkgrey);
  position: sticky;

  ul {
    margin: 0;
    margin-right: 2rem;
    display: flex;
    justify-content: space-around;
    list-style: none;
    font-size: 4rem;
  }

  li {
    margin-left: 1rem;
  }
`

const NavItems = styled.div`
  width: 100%;
  display: flex;
  justify-content: space-between;
  padding-top: 0.5rem;
  padding-bottom: 0.5rem;

  a {
    text-decoration: none;
  }
`

// Code

class Nav extends Component {
  render() {
    return (
      <StyledNav>
        <NavItems>
          <i className="fas fa-code" />
          <ul>
            <li>
              <a href="#home">About</a>
            </li>
            <li>
              <a href="#work">Work</a>
            </li>
            <li>Contact</li>
          </ul>
        </NavItems>
      </StyledNav>
    )
  }
}

export default Nav
