import styled from 'styled-components'

const StyledWork = styled.div`

  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  padding-top: 2vw;
  padding-bottom: 2vw;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  text-align: center;
  `

const HeaderContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
`

const Header = styled.h2`
margin: 3rem 0 2rem 0;
  font-size: 6vw;
  color: white;
  font-style: italic;
`

export { StyledWork, HeaderContainer, Header }
